  static const double spacingXXl = baseUnit * 6; // 48px if base is 8px

  // --------------------------------------------------------------------------
  // Component Padding Standards
  // --------------------------------------------------------------------------
  /// Standard button padding (vertical, horizontal)
  static const EdgeInsets buttonPadding = EdgeInsets.symmetric(
    vertical: spacingSm,
    horizontal: spacingMd,
  );

  /// Standard card padding
  static const EdgeInsets cardPadding = EdgeInsets.all(spacingMd);

  /// Standard list item padding
  static const EdgeInsets listItemPadding = EdgeInsets.symmetric(
    vertical: spacingSm,
    horizontal: spacingMd,
  );

  /// Standard input field padding
  static const EdgeInsets inputPadding = EdgeInsets.all(spacingMd);

  /// Standard screen horizontal padding
  static const EdgeInsets screenPadding = EdgeInsets.symmetric(
    horizontal: spacingMd,
  );

