  static const Curve animationCurveEmphasized = Curves.easeOutCubic;

  // ==========================================================================
  // BREAKPOINTS (for responsive design)
  // ==========================================================================

  /// Small phone breakpoint
  static const double breakpointSmall = 320.0;

  /// Standard phone breakpoint
  static const double breakpointMedium = 375.0;

  /// Large phone breakpoint
  static const double breakpointLarge = 414.0;

  /// Tablet breakpoint
  static const double breakpointTablet = 768.0;

  /// Desktop breakpoint
  static const double breakpointDesktop = 1024.0;

  // ==========================================================================
  // HELPER METHODS
  // ==========================================================================

  /// Returns appropriate spacing based on device size
  static double responsiveSpacing(BuildContext context, {
    double small = spacingSm,
    double medium = spacingMd,
    double large = spacingLg,
  }) {
    final width = MediaQuery.of(context).size.width;
    if (width < breakpointMedium) return small;
    if (width < breakpointLarge) return medium;
    return large;
  }

  /// Returns whether the screen is considered small
  static bool isSmallScreen(BuildContext context) {
    return MediaQuery.of(context).size.width < breakpointMedium;
  }

  /// Returns whether the screen is considered large
  static bool isLargeScreen(BuildContext context) {
    return MediaQuery.of(context).size.width >= breakpointTablet;
  }
